import {ChangeDetectorRef, Component, OnInit} from '@angular/core';
import {Contact} from '../../shared/models/contacts.model';
import {CrossComponentService} from '../../services/cross-component.service';

@Component({
  selector: 'app-contacts',
  templateUrl: './contacts.component.html',
  styleUrls: ['./contacts.component.scss']
})
export class ContactsComponent implements OnInit {
  contactList: Contact[];
  stopEmission: boolean;

  // hide the bar at the bottom & show the message bar
  switchToMessageWindow() {
    if (this.stopEmission) {
      return;
    } else {
      this.crossComponentService.showMessageBar$.next(true);
      this.stopEmission = true;
    }
  }

  constructor(private crossComponentService: CrossComponentService,
              private cdr: ChangeDetectorRef) { }

  ngOnInit() {
    this.contactList = [
      {
        photoUrl: 'data:image/jpeg;base64,',
        userName: 'first username',
        lastMessage: 'first username message'
      },
      {
        photoUrl: 'data:image/jpeg;base64,',
        userName: 'second username',
        lastMessage: 'second username message'
      },
      {
        photoUrl: 'data:image/jpeg;base64,',
        userName: 'third username',
        lastMessage: 'third username message'
      }
    ];
    // needed the extra trigger to load the elements in the UI on initial load
    this.cdr.detectChanges();
  }

}
